import { Component } from 'react';
import Loading from '../components/Loading';
import { getAddress } from '../services/apiService';

class Search extends Component {
  state = { 
    isLoading: false,
    cepSearch: {},
    cepTerm: ''
  }

  componentDidUpdate(prevState) {
    const { cepTerm } = this.state;
    if (prevState.cepTerm !== cepTerm) {
      this.loadAddressData(cepTerm);
    }
  }

  loadAddressData = async (cep) => {
    const address = await getAddress(cep);
    this.setState({
      cepSearch: address,
      isLoading: false,
    })
  }

  handleChangeCEP = ({ target }) => {
    this.setState({
      cepTerm: target.value,
      isLoading: true,
    });
  }

  render() { 
    const { cepTerm, cepSearch, isLoading } = this.state;
    return (
      <div className='search-container'>
        { isLoading ? (
          <Loading /> 
        ) : (
          <>
            <h1>{`Busca por CEP ${cepTerm}`}</h1>
            <div className='form-container'>
              <input
                type="text"
                placeholder="Informe o CEP"
                value={ cepTerm }
              />
              <button
                type='button'
                onClick={ this.handleChangeCEP }
              >
                Pesquisar
              </button>
            </div>
          </>
        )}
      </div>
    );
  }
}

export default Search;
